memberdef.initializer:
= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    t /= d;
    return c * t + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    t /= d;
    return c * t * t + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    t /= d;
    return -c * t * (t - 2) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    t /= d / 2;
    if (t < 1) {
        return c/2*t*t + b;
    }

    return -c/2 * ((t-1)*(t-3) - 1) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    t /= d;
    return c*t*t*t + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    t = t/d - 1;
    return c*(t*t*t + 1) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    t /= d / 2;
    if (t < 1) {
        return c/2*t*t*t + b;
    }

    t -= 2;
    return c/2*(t*t*t + 2) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    t /= d;
    return c*t*t*t*t + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    t = t/d - 1;
    return -c * (t*t*t*t - 1) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    t /= d / 2;
    if (t < 1) {
        return c/2*t*t*t*t + b;
    }

    t -= 2;
    return -c / 2 * (t*t*t*t - 2) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
