    t /= d / 2;
    if (t < 1) {
        return c/2*t*t*t*t*t + b;
    }

    t -= 2;
    return c/2*(t*t*t*t*t + 2) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    return -c * cos(t/d * (M_PI/2)) + c + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    return c * sin(t/d * (M_PI/2)) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    return -c/2 * (cos(M_PI*t/d) - 1) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    return (t==0) ? b : c * pow(2, 10 * (t/d - 1)) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    return (t==d) ? b+c : c * (-pow(2, -10 * t/d) + 1) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    if (t == 0) {
        return b;
    }

    if (t == d) {
        return b+c;
    }

    t /= d / 2;

    if (t < 1) {
        return c/2 * pow(2, 10 * (t - 1)) + b;
    }

    --t;
    return c/2 * (-pow(2, -10 * t) + 2) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    t /= d;
    return -c * (sqrt(1 - t*t) - 1) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    t = t / d - 1;
    return c * sqrt(1 - t*t) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    t /= d / 2;
    if (t < 1) {
        return -c/2 * (sqrt(1 - t*t) - 1) + b;
    }

    t -= 2;
    return c/2 * (sqrt(1 - t*t) + 1) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    if (!c) return b;

    CGFloat amplitude = 5;
    CGFloat period = 0.3;
    CGFloat s = 0;

    if (t == 0) return b;

    t /= d;

    if (t == 1) return b+c;

    if (!period) {
        period = d * .3;
    }

    if (amplitude < fabs(c)) {
        amplitude = c;
        s = period / 4;
    }
    else {
        s = period/(2*M_PI) * asin (c/amplitude);
    }

    t -= 1;
    return -(amplitude*pow(2,10*t) * sin( (t*d-s)*(2*M_PI)/period )) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    if (!c) return b;

    CGFloat amplitude = 5;
    CGFloat period = 0.3;
    CGFloat s = 0;
    if (t == 0) {
        return b;
    }

    t /= d;
    if (t == 1) {
        return b + c;
    }

    if (!period) {
        period = d * .3;
    }

    if (amplitude < fabs(c)) {
        amplitude = c;
        s = period / 4;
    }
    else {
        s = period / (2 * M_PI) * sin(c / amplitude);
    }

    return (amplitude * pow(2, -10 * t) * sin((t * d - s) * (2 * M_PI) / period) + c + b);
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    CGFloat s = 1.70158;
    t /= d;
    return c*t*t*((s+1)*t - s) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    CGFloat s = 1.70158;
    t = t/d - 1;
    return c*(t*t*((s+1)*t + s) + 1) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    CGFloat s = 1.70158 * 1.525;
    t /= d / 2;
    if (t < 1) {
        return c/2*(t*t*((s + 1)*t - s)) + b;
    }

    t -= 2;
    return c/2 * (t * t * ((s + 1) * t + s) + 2) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    return c - easeOutBounce(d-t, 0, c, d) + b;
}

= ^CGFloat(CGFloat t, CGFloat b, CGFloat c, CGFloat d) {
    if (t < d/2) return easeInBounce (t*2, 0, c, d) * .5 + b;
    return easeOutBounce (t*2-d, 0, c, d) * .5 + c*.5 + b;
}
